mapper.insert:
    INSERT INTO restaurant_member
	    (restaurant_num
	    , member_id
	    , restaurant_name
	    , restaurant_ename
	    , restaurant_sectors
	    , restaurant_comment
	    , restaurant_phone
	    , restaurant_address
	    )
    VALUES
	    (restaurant_member_seq.nextval
	    , #{member_id}
	    , #{restaurant_name}
	    , #{restaurant_ename}
	    , #{restaurant_sectors}
	    , #{restaurant_comment}
	    , #{restaurant_phone}
	    , #{restaurant_address}
    )

    INSERT INTO restaurant_file
	    (restaurant_file_num
	    , restaurant_originalfile
	    , restaurant_savedfile
	    , restaurant_num
	    )
    VALUES
	    (restaurant_file_seq.nextval
	    , #{restaurant_originalfile}
	    , #{restaurant_savedfile}
	    , #{restaurant_num}
    )
