, member_tags
,
member_address1
, member_address2
, member_gender
, member_nickname

	, member_originalfile
	, member_savedfile

)
VALUES
(user_member_seq.nextval
, #{member_id}
, #{member_pw}
,
#{member_birth}
, #{member_name}
, #{member_phone}
, #{member_email}
,
#{member_tags}
, #{member_address1}
, #{member_address2}
,
#{member_gender}
, #{member_nickname}

	, #{member_originalfile}
	, #{member_savedfile}

)

mapper.update:
UPDATE user_member SET member_pw = #{member_pw}, member_name = #{member_name}, member_nickname = #{member_nickname}, member_email = #{member_email},
member_phone = #{member_phone}, member_address1 = #{member_address1}, member_address2 = #{member_address2}, member_birth = #{member_birth}, member_tags = #{member_tags}
, member_originalfile = #{member_originalfile} , member_savedfile = #{member_savedfile}
WHERE member_id = #{member_id}

